function SplashDamageClass:getDistance(e,t)
local a=e.x
local o=e.y
local e=e.z
local o=t.x
local i=t.y
local i=t.z
local t=math.abs(a-o)
local e=math.abs(e-i)
local e=math.sqrt(t*t+e*e)
return e
end
